// SPDX-License-Identifier: MIT
pragma solidity >=0.8.0 <0.9.0;

/**
 * @title AgentPayment
 * @dev 一个简单的代理支付合约
 */
contract AgentPayment {
    // 事件
    event PaymentReceived(address indexed from, uint256 amount, string message);
    event PaymentWithdrawn(address indexed to, uint256 amount);

    // 状态变量
    address public owner;
    uint256 public totalPayments;
    mapping(address => uint256) public userPayments;

    // 修饰符
    modifier onlyOwner() {
        require(msg.sender == owner, "Only owner can call this function");
        _;
    }

    // 构造函数
    constructor(address initialOwner) {
        owner = initialOwner;
    }

    /**
     * @dev 接收支付
     * @param message 支付消息
     */
    function makePayment(string memory message) external payable {
        require(msg.value > 0, "Payment amount must be greater than 0");

        totalPayments += msg.value;
        userPayments[msg.sender] += msg.value;

        emit PaymentReceived(msg.sender, msg.value, message);
    }

    /**
     * @dev 获取合约余额
     */
    function getBalance() external view returns (uint256) {
        return address(this).balance;
    }

    /**
     * @dev 获取用户总支付金额
     */
    function getUserTotalPayment(address user) external view returns (uint256) {
        return userPayments[user];
    }

    /**
     * @dev 提取合约中的资金（仅所有者）
     */
    function withdraw() external onlyOwner {
        uint256 balance = address(this).balance;
        require(balance > 0, "No funds to withdraw");

        (bool success, ) = payable(owner).call{value: balance}("");
        require(success, "Withdrawal failed");

        emit PaymentWithdrawn(owner, balance);
    }

    /**
     * @dev 接收 ETH
     */
    receive() external payable {}
}

contracts/AgentTaskManager.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity >=0.8.0 <0.9.0;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

/**
 * @title AgentTaskManager
 * @dev AI Agent 任务市场合约
 * 实现任务创建、接受、完成、审核和支付的核心逻辑
 */
contract AgentTaskManager is ReentrancyGuard, Ownable {
    // 任务状态枚举
    enum TaskState { Open, InProgress, Completed, Approved }

    // 任务结构体
    struct Task {
        uint256 id;
        address creator;    // 老板 Agent
        address worker;     // 工作 Agent  
        string prompt;      // 任务描述，例如图片生成的提示词
        string resultURI;   // 工作成果的链接 (例如图片 URL)
        uint256 reward;     // 奖励金额 (使用 ERC20 稳定币)
        TaskState state;    // 任务状态
        uint256 createdAt;  // 创建时间
        uint256 updatedAt;  // 更新时间
    }

    // 状态变量
    IERC20 public immutable stablecoin;  // 稳定币合约地址
    Task[] public tasks;                 // 存储所有任务的数组
    mapping(address => uint256[]) public creatorTasks;  // 创建者的任务映射
    mapping(address => uint256[]) public workerTasks;   // 工作者的任务映射

    // 事件
    event TaskCreated(uint256 indexed taskId, address indexed creator, string prompt, uint256 reward);
    event TaskAccepted(uint256 indexed taskId, address indexed worker);
    event TaskCompleted(uint256 indexed taskId, string resultURI);
    event TaskApproved(uint256 indexed taskId, address indexed worker, uint256 reward);
    event PaymentSent(address indexed to, uint256 amount);

    // 错误定义
    error TaskNotFound();
    error TaskNotOpen();
    error TaskNotInProgress();
    error TaskNotCompleted();
    error NotTaskCreator();
    error NotTaskWorker();
    error InsufficientAllowance();
    error TransferFailed();
    error InvalidReward();
    error InvalidPrompt();

    /**
     * @dev 构造函数
     * @param _stablecoin 稳定币合约地址
     */
    constructor(address _stablecoin) Ownable(msg.sender) {
        require(_stablecoin != address(0), "Invalid stablecoin address");
        stablecoin = IERC20(_stablecoin);
    }

    /**
     * @dev 创建任务
     * @param _prompt 任务描述
     * @param _reward 奖励金额
     */
    function createTask(string memory _prompt, uint256 _reward) external nonReentrant {
        if (bytes(_prompt).length == 0) revert InvalidPrompt();
        if (_reward == 0) revert InvalidReward();

        // 检查授权额度
        if (stablecoin.allowance(msg.sender, address(this)) < _reward) {
            revert InsufficientAllowance();
        }

        // 转移资金到合约
        if (!stablecoin.transferFrom(msg.sender, address(this), _reward)) {
            revert TransferFailed();
        }

        // 创建新任务
        uint256 taskId = tasks.length;
        Task memory newTask = Task({
            id: taskId,
            creator: msg.sender,
            worker: address(0),
            prompt: _prompt,
            resultURI: "",
            reward: _reward,
            state: TaskState.Open,
            createdAt: block.timestamp,
            updatedAt: block.timestamp
        });

        tasks.push(newTask);
        creatorTasks[msg.sender].push(taskId);

        emit TaskCreated(taskId, msg.sender, _prompt, _reward);
    }

    /**
     * @dev 接受任务
     * @param _taskId 任务ID
     */
    function acceptTask(uint256 _taskId) external {
        if (_taskId >= tasks.length) revert TaskNotFound();

        Task storage task = tasks[_taskId];
        if (task.state != TaskState.Open) revert TaskNotOpen();
        if (task.creator == msg.sender) revert NotTaskCreator(); // 创建者不能接受自己的任务

        task.worker = msg.sender;
        task.state = TaskState.InProgress;
        task.updatedAt = block.timestamp;

        workerTasks[msg.sender].push(_taskId);

        emit TaskAccepted(_taskId, msg.sender);
    }

    /**
     * @dev 完成任务
     * @param _taskId 任务ID
     * @param _resultURI 结果链接
     */
    function completeTask(uint256 _taskId, string memory _resultURI) external {
        if (_taskId >= tasks.length) revert TaskNotFound();

        Task storage task = tasks[_taskId];
        if (task.state != TaskState.InProgress) revert TaskNotInProgress();
        if (task.worker != msg.sender) revert NotTaskWorker();
        if (bytes(_resultURI).length == 0) revert InvalidPrompt();

        task.resultURI = _resultURI;
        task.state = TaskState.Completed;
        task.updatedAt = block.timestamp;

        emit TaskCompleted(_taskId, _resultURI);
    }

    /**
     * @dev 审核并支付任务
     * @param _taskId 任务ID
     */
    function approveTask(uint256 _taskId) external nonReentrant {
        if (_taskId >= tasks.length) revert TaskNotFound();

        Task storage task = tasks[_taskId];
        if (task.state != TaskState.Completed) revert TaskNotCompleted();
        if (task.creator != msg.sender) revert NotTaskCreator();

        task.state = TaskState.Approved;
        task.updatedAt = block.timestamp;

        // 支付奖励给工作者
        if (!stablecoin.transfer(task.worker, task.reward)) {
            revert TransferFailed();
        }

        emit TaskApproved(_taskId, task.worker, task.reward);
        emit PaymentSent(task.worker, task.reward);
    }

    // ========== 查询函数 ==========

    /**
     * @dev 获取任务详情
     * @param _taskId 任务ID
     * @return 任务详情
     */
    function getTask(uint256 _taskId) external view returns (Task memory) {
        if (_taskId >= tasks.length) revert TaskNotFound();
        return tasks[_taskId];
    }

    /**
     * @dev 获取任务总数
     * @return 任务总数
     */
    function getTaskCount() external view returns (uint256) {
        return tasks.length;
    }

    /**
     * @dev 获取用户创建的任务ID列表
     * @param _creator 创建者地址
     * @return 任务ID数组
     */
    function getTasksByCreator(address _creator) external view returns (uint256[] memory) {
        return creatorTasks[_creator];
    }

    /**
     * @dev 获取用户接受的任务ID列表
     * @param _worker 工作者地址
     * @return 任务ID数组
     */
    function getTasksByWorker(address _worker) external view returns (uint256[] memory) {
        return workerTasks[_worker];
    }

    /**
     * @dev 获取所有任务（分页）
     * @param _offset 偏移量
     * @param _limit 限制数量
     * @return 任务数组
     */
    function getAllTasks(uint256 _offset, uint256 _limit) external view returns (Task[] memory) {
        uint256 totalTasks = tasks.length;
        if (_offset >= totalTasks) {
            return new Task[](0);
        }

        uint256 end = _offset + _limit;
        if (end > totalTasks) {
            end = totalTasks;
        }

        uint256 resultLength = end - _offset;
        Task[] memory result = new Task[](resultLength);

        for (uint256 i = 0; i < resultLength; i++) {
            result[i] = tasks[_offset + i];
        }

        return result;
    }

    // ========== 权限检查函数 ==========

    /**
     * @dev 检查用户是否为任务创建者
     * @param _taskId 任务ID
     * @param _user 用户地址
     * @return 是否为创建者
     */
    function isTaskCreator(uint256 _taskId, address _user) external view returns (bool) {
        if (_taskId >= tasks.length) return false;
        return tasks[_taskId].creator == _user;
    }

    /**
     * @dev 检查用户是否为任务工作者
     * @param _taskId 任务ID
     * @param _user 用户地址
     * @return 是否为工作者
     */
    function isTaskWorker(uint256 _taskId, address _user) external view returns (bool) {
        if (_taskId >= tasks.length) return false;
        return tasks[_taskId].worker == _user;
    }

    /**
     * @dev 检查任务是否可被接受
     * @param _taskId 任务ID
     * @param _user 用户地址
     * @return 是否可接受
     */
    function canAcceptTask(uint256 _taskId, address _user) external view returns (bool) {
        if (_taskId >= tasks.length) return false;
        Task memory task = tasks[_taskId];
        return task.state == TaskState.Open && task.creator != _user;
    }

    /**
     * @dev 检查任务是否可被完成
     * @param _taskId 任务ID
     * @param _user 用户地址
     * @return 是否可完成
     */
    function canCompleteTask(uint256 _taskId, address _user) external view returns (bool) {
        if (_taskId >= tasks.length) return false;
        Task memory task = tasks[_taskId];
        return task.state == TaskState.InProgress && task.worker == _user;
    }

    /**
     * @dev 检查任务是否可被审核
     * @param _taskId 任务ID
     * @param _user 用户地址
     * @return 是否可审核
     */
    function canApproveTask(uint256 _taskId, address _user) external view returns (bool) {
        if (_taskId >= tasks.length) return false;
        Task memory task = tasks[_taskId];
        return task.state == TaskState.Completed && task.creator == _user;
    }
}

contracts/MockUSDC.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity >=0.8.0 <0.9.0;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

/**
 * @title MockUSDC
 * @dev 用于测试的 Mock USDC 代币
 */
contract MockUSDC is ERC20, Ownable {
    uint8 private _decimals = 6; // USDC 使用 6 位小数

    constructor() ERC20("Mock USDC", "mUSDC") Ownable(msg.sender) {
        // 给部署者铸造一些初始代币
        _mint(msg.sender, 1000000 * 10**6); // 1,000,000 USDC
    }

    /**
     * @dev 返回代币小数位数
     */
    function decimals() public view virtual override returns (uint8) {
        return _decimals;
    }

    /**
     * @dev 铸造代币（仅所有者）
     * @param to 接收地址
     * @param amount 铸造数量
     */
    function mint(address to, uint256 amount) external onlyOwner {
        _mint(to, amount);
    }

    /**
     * @dev 销毁代币
     * @param amount 销毁数量
     */
    function burn(uint256 amount) external {
        _burn(msg.sender, amount);
    }
}
